mapper.select:
SELECT * 
FROM (SELECT rownum rn, a.* 
     FROM (SELECT *
           FROM ${tableName}
           ORDER BY qna_no DESC
          ) a
      )
 WHERE rn BETWEEN #{start} AND #{end}

mapper.update:
UPDATE ${tableName}
SET title = #{title, jdbcType=VARCHAR}, content = #{content, jdbcType=VARCHAR}, updatedate = sysdate
WHERE qna_no = #{qnaNo}
