mapper.update:
UPDATE ${tableName}
SET pickeddate = sysdate
WHERE qna_no = #{qnaNo} AND comment_no = #{commentNo}

UPDATE ${tableName}
SET content = #{content, jdbcType=VARCHAR}, regdate = sysdate
WHERE qna_no = #{qnaNo} AND comment_no = #{commentNo}

UPDATE ${tableName}
SET comment_status = 0
WHERE qna_no = #{qnaNo} AND comment_no = #{commentNo}
